/**
 * Class EB_Icon_Loader
 */
class EB_Icon_Loader {

	/**
	 * Gets the SVG code for a given icon.
	 *
	 * @param $icon string icon name
	 * @param $width int icon width
	 * @param $height int icon height
	 *
	 * @return null|string
	 */
	public static function get_svg( $icon, $width, $height ) {
		// icons array
		$arr = self::$social_icons;

		// if icon exist in icons array
		if ( array_key_exists( $icon, $arr ) ) {
			$repl = sprintf( '<svg class="svg-icon" width="%d" height="%d" aria-hidden="true" role="img" focusable="false" ', $width, $height );
			$svg  = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) ); // Add extra attributes to SVG code.
			$svg  = preg_replace( "/([\n\t]+)/", ' ', $svg ); // Remove newlines & tabs.
			$svg  = preg_replace( '/>\s*</', '><', $svg ); // Remove white space between SVG tags.

			return $svg;
		}

		// else return null
		return null;
	}

